import { fetchChannelMessage, findChannel } from "@/client";
import { CTF } from "@/data/entities/ctf";
import Jimp from "jimp";
import { Command, CommandoClient, CommandoMessage, FriendlyError } from "discord.js-commando";
import { MessageAttachment } from "discord.js";

export default class Bingo extends Command {
  constructor(client: CommandoClient) {
    super(client, {
      name: "bingo",
      group: "misc",
      memberName: "bingo",
      description: "Play Bad CTF Bingo",
      args: [
        {
          key: "x",
          prompt: "Enter zero-indexed x-index of the bingo card (0-4)",
          type: "integer",
          default: -1
        },
        {
          key: "y",
          prompt: "Enter zero-indexed y-index of the bingo card (0-4)",
          type: "integer",
          default: -1
        },
      ]
    });
  }

  async image() {
    const img = await Jimp.read(Buffer.from("", 'base64'));
    return img;
  }

  async write(bingo: Record<string, any>[][]) {
    const font = await Jimp.loadFont(Jimp.FONT_SANS_16_BLACK);
    const img = await this.image();
    const idx = [
      [[70, 195], [156, 195], [250, 195], [335, 195], [431, 195]],
      [[70, 226], [156, 296], [250, 296], [335, 296], [427, 226]],
      [[70, 385], [156, 385], [250, 385], [335, 385], [431, 385]],
      [[70, 485], [156, 485], [250, 485], [335, 485], [431, 485]],
      [[70, 593], [156, 529], [250, 593], [335, 593], [431, 593]],
    ];
    for(let i = 0 ; i < 5; i++) {
        for(let j = 0; j < 5; j++) {
            const n = Object.keys(bingo[i][j]).length;
            const [x, y] = idx[i][j];
            if (n !== 0) {

                img.print(font, x, y, n)
            }
        }
    }
    return await img.getBufferAsync(Jimp.MIME_PNG);
  }

  async run(message: CommandoMessage, {x, y}: {x:number, y: number}) {
    // TODO move all this common code into one file.
    const ctf = await CTF.findOne({ where: { "discord.channel": message.channel.id, archived: false } });
    if(!ctf) {
      throw new FriendlyError("This channel is not a valid CTF channel.");
    }
    if (x >= 0 && x < 5 && y >= 0 && y < 5) {
      ctf.bingo[y][x][message.author.id] = 1;
      await ctf.save();
    }
    // TODO marking here
    const img = await this.write(ctf.bingo);
    return await message.say("Use `!bingo {x} {y}` to set the bingo", new MessageAttachment(img, "nani.png"));
  }
}

